mapper.select:
        SELECT


--         级联表的字段

        FROM exampaper  a



                and a.id in

                #{item}



                and a.exampaper_name like CONCAT('%',#{params.exampaperName},'%')


                  and a.exampaper_date >= #{params.exampaperDateStart}   


                  and a.exampaper_date <= #{params.exampaperDateEnd}   


                and a.exampaper_date = #{params.exampaperDate}


                and a.exampaper_types = #{params.exampaperTypes}




        order by a.${params.orderBy} desc
