mapper.select:
SELECT
-- 级联表的字段 ,exampaper.exampaper_name as exampaperName ,exampaper.exampaper_date as exampaperDate ,exampaper.exampaper_types as exampaperTypes ,yonghu.yonghu_name as yonghuName ,yonghu.yonghu_id_number as yonghuIdNumber ,yonghu.yonghu_phone as yonghuPhone ,yonghu.yonghu_photo as yonghuPhoto
FROM examrecord a left JOIN exampaper exampaper ON a.exampaper_id = exampaper.id left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
and a.id in
#{item}
and a.examrecord_uuid_number like CONCAT('%',#{params.examrecordUuidNumber},'%')
and a.yonghu_id = #{params.yonghuId}
and a.exampaper_id = #{params.exampaperId}
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and exampaper.exampaper_name like CONCAT('%',#{params.exampaperName},'%')
and exampaper.exampaper_date >= #{params.exampaperDateStart}
and exampaper.exampaper_date <= #{params.exampaperDateEnd}
and exampaper.exampaper_date = #{params.exampaperDate}
and exampaper.exampaper_types = #{params.exampaperTypes}
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')
and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
order by a.${params.orderBy} desc
