mapper.select:
SELECT
-- 级联表的字段 ,examquestion.exampaper_id as exampaperId ,examquestion.examquestion_name as examquestionName ,examquestion.examquestion_options as examquestionOptions ,examquestion.examquestion_types as examquestionTypes ,examquestion.examquestion_sequence as examquestionSequence ,yonghu.yonghu_name as yonghuName ,yonghu.yonghu_id_number as yonghuIdNumber ,yonghu.yonghu_phone as yonghuPhone ,yonghu.yonghu_photo as yonghuPhoto
FROM examredetails a left JOIN examquestion examquestion ON a.examquestion_id = examquestion.id left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
and a.id in
#{item}
and a.examredetails_uuid_number like CONCAT('%',#{params.examredetailsUuidNumber},'%')
and a.yonghu_id = #{params.yonghuId}
and a.examquestion_id = #{params.examquestionId}
and a.examredetails_myanswer like CONCAT('%',#{params.examredetailsMyanswer},'%')
and examquestion.examquestion_name like CONCAT('%',#{params.examquestionName},'%')
and examquestion.examquestion_options like CONCAT('%',#{params.examquestionOptions},'%')
and examquestion.examquestion_types = #{params.examquestionTypes}
and examquestion.examquestion_sequence >= #{params.examquestionSequenceStart}
and examquestion.examquestion_sequence <= #{params.examquestionSequenceEnd}
and examquestion.examquestion_sequence = #{params.examquestionSequence}
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')
and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
order by a.${params.orderBy} desc
